      { 
        "name": "Navigate to 'https://www.uptrends.com/demos'",
        "ignoreErrors": false, 
        "recordWaterfall": false, 
        "actions": [ 
        { 
        "navigate": { 
        "url": "https://www.uptrends.com/demos" 
        } 
      }, 
      { 
        "testDocumentContent": { 
          "value": "Attend a webinar", 
          "testType": "Contains" 
          } 
