              "value": "dedicated server",
              "testType": "Contains" 
            }
          }
        ]
      },
      {
        "name": "Switch to first tab",
        "actions": [
          { "switchBrowserTab":  
            {  
              "windowTitle": "https://blog.uptrends.com"
            }
          },
          {  
            "testDocumentContent":
              {
                "value": "Attend a webinar",
                "testType": "Contains"
              }
            }
          ]
        }
      ]  
    }

